import { useState, useEffect, onChange } from "react";

function AddToDo() {
    const [formData, setFormData] = useState({title:""}) 

    function handleTitleChange(event) {
       setFormData({title: event.target.value})
    }

    function createTitle() {
      console.log(formData.title)
      return(
         <li>{formData.title}</li>
      )
    }

    const onTitleChange = (event) => {
      setFormData({...formData, [event.target.name] : event.target.value})
   }

    return (
    <div>
    <header>
    <h1>To Do List:</h1>
    </header>
    <form id="add-todo-form">
    <label for="add">Title:</label>
    <div id="add-bar">
    <input value={formData.title} onChange={onTitleChange}  id="bar" name="add" type="text"/>
    <input onClick={createTitle} className="submit" type="submit" value="Add"></input>
    </div>
    </form>
     <ul id="to-do-list">
     </ul>
    </div>
    )
 }

export default AddToDo;
